		private ArrayList ScaleData(Rectangle mapArea)
		{

			ArrayList scaledData = new ArrayList();
			foreach(PointF[] areaSegment in mapData)
			{
				PointF[] scaledPoints = new PointF[areaSegment.Length];
				for (int i=0;i<areaSegment.Length;i++)
				{
					scaledPoints[i] = Scale(areaSegment[i], mapArea);
				}

				scaledData.Add(scaledPoints);
			}

			return scaledData;
		}

		private ArrayList ScaleLinesData(Rectangle mapArea)
		{

			ArrayList scaledData = new ArrayList();
			foreach(PointF[] lineSegment in mapLines)
			{
				PointF[] scaledPoints = new PointF[lineSegment.Length];
				for (int i=0;i<lineSegment.Length;i++)
				{
					scaledPoints[i] = Scale(lineSegment[i], mapArea);
				}

				scaledData.Add(scaledPoints);
			}

			return scaledData;
		}

		private PointF Scale(PointF point, Rectangle mapArea)
		{
			PointF p = new PointF(mapArea.X + point.X*mapArea.Width/1000, mapArea.Y + point.Y*mapArea.Height/1000);
			return p;
		}

