		/// <summary>
		/// Paints the map element.
		/// </summary>
		/// <param name="g">The Graphics object to draw</param>
		/// <remarks>Causes the map element to be painted to the specified device.</remarks>
		public override void Paint(Graphics g)
		{			
			//mapRegion = this.GetRegion();
			//mapRegion.Offset(-rBounds.X, -rBounds.Y);

			//Rectangle mapArea = mapRegion;
			Rectangle mapArea = this.Section.Document.DesignMode ? theRegion : Bounds;

			if (backgroundColor != Color.Transparent)
			{
				if (paintTransparent)				
					g.FillRectangle(new SolidBrush(ShiftColor(backgroundColor)), mapArea);
				else
					g.FillRectangle(new SolidBrush(backgroundColor), mapArea);
			}

			if ( this.borderWidth > 0 )
			{				
				Pen borderPen = new Pen(borderColor, borderWidth);
				borderPen.Alignment = PenAlignment.Inset;
				g.DrawRectangle(borderPen, mapArea);
				borderPen.Dispose();
			}
			
			PaintBorders(g, mapArea);
			PaintLines(g, mapArea);

			g.SetClip(new Region(mapArea), System.Drawing.Drawing2D.CombineMode.Intersect);
			PaintPoints(g, mapArea);
			g.ResetClip();
		}


		/// <summary>
		/// Clones the structure of the map element, including all properties.
		/// </summary>
		/// <returns><see cref="Com.Delta.PrintManager.Engine.TextField">daReport.TextField</see></returns>
		public override object Clone()
		{			
			Map tmp = new Map(this.X, this.Y, this.Width, this.Height, this.section);
			tmp.Layout = this.Layout;
			tmp.foregroundColor = this.foregroundColor;
			tmp.backgroundColor = this.backgroundColor;
			tmp.MapType = this.MapType;
			tmp.BorderColor = this.BorderColor;
			tmp.BorderWidth = this.BorderWidth;
			tmp.ExportQuality = this.ExportQuality;

			MapPoint[] pts = new MapPoint[this.mapPoints.Length];
			for (int i=0;i<this.mapPoints.Length;i++)
			{
				pts[i] = (MapPoint)mapPoints[i].Clone();
			}

			tmp.Landmarks = pts;

			tmp.Name = "map" + tmp.GetHashCode().ToString();

			return tmp;
		}

		private void PaintLines(Graphics g, Rectangle mapArea)
		{
			ArrayList scaledData = ScaleLinesData(mapArea);
			foreach(PointF[] lineSegment in scaledData)
			{
				g.DrawLines(Pens.DarkGray, lineSegment);					
			}
		}

		private void PaintBorders(Graphics g, Rectangle mapArea)
		{
			ArrayList scaledData = ScaleData(mapArea);

			foreach(PointF[] areaSegment in scaledData)
			{
				if (foregroundColor != Color.Transparent)
				{
					if (paintTransparent)				
						g.FillPolygon(new SolidBrush(ShiftColor(foregroundColor)), areaSegment);
					else
						g.FillPolygon(new SolidBrush(foregroundColor), areaSegment);
				}
				
				g.DrawPolygon(Pens.DarkGray, areaSegment);
								
			}
		}

		private void PaintPoints(Graphics g, Rectangle mapArea)
		{
			
			foreach (MapPoint point in mapPoints)
			{
				float sizeInPixels = this.PointsToPixels(point.Font.SizeInPoints);
				Font pointFont = new Font(point.Font.FontFamily, sizeInPixels*g.PageScale, point.Font.Style, GraphicsUnit.Pixel);

				PointF p = Scale(new PointF(point.X, point.Y), mapArea);
				float offset = 1;

				if (point.ShowMarker)
					g.FillRectangle(new SolidBrush(point.Color), p.X - offset, p.Y - offset, 3, 3);

				if (point.Text != string.Empty)
				{
					float h = pointFont.GetHeight();
					SizeF size = g.MeasureString(point.Text, pointFont);
					if (p.X + 4*offset + size.Width > mapArea.Right)
						g.DrawString(point.Text, pointFont, new SolidBrush(point.Color), p.X - 2*offset - size.Width, p.Y - h/2);
					else
						g.DrawString(point.Text, pointFont, new SolidBrush(point.Color), p.X + 4*offset, p.Y - h/2);
				}
			}
		}

