		/// <summary>
		/// Map image for printing and exporting purpose
		/// </summary>
		[Browsable(false)]
		public Bitmap Image
		{
			get 
			{
				Rectangle paintArea = this.Section.Document.DesignMode ? theRegion : Bounds;

				Bitmap mapImage = new Bitmap(paintArea.Width + (borderWidth==1? 1 : 0), paintArea.Height + (borderWidth==1? 1 : 0));

				Graphics imageGraphics = Graphics.FromImage(mapImage);
				
				Matrix m = new Matrix();
				m.Translate(-paintArea.X, -paintArea.Y, MatrixOrder.Append);
				imageGraphics.Transform = m;

				this.Paint(imageGraphics);
				imageGraphics.Dispose();

				return mapImage;
			}
		}

		internal static MapTypes GetMapType(string type)
		{
			try
			{				
				return (MapTypes)Enum.Parse(typeof(MapTypes), type, false); 
			}
			catch (Exception)
			{
				return MapTypes.Serbia;			
			}			
		}

		internal void PaintForEdit(Graphics g, int size)
		{
			Rectangle mapArea = new Rectangle(0, 0, size, size);
			
			PaintBorders(g, mapArea);
			PaintPoints(g, mapArea);
		}

		private void AddSegment(float[] x, float[] y)
		{
			ArrayList tmp = new ArrayList();

			for (int i=0;i<x.Length;i++)
				tmp.Add(new PointF(x[i], y[i]));
					
			mapData.Add(tmp.ToArray(typeof(PointF)));
		}

		private void AddSegment(short[] x, short[] y)
		{
			ArrayList tmp = new ArrayList();

			for (int i=0;i<x.Length;i++)
				tmp.Add(new PointF( (float)x[i]/10, (float)y[i]/10));
					
			mapData.Add(tmp.ToArray(typeof(PointF)));
		}

		private void AddLine(float[] x, float[] y)
		{
			ArrayList tmp = new ArrayList();

			for (int i=0;i<x.Length;i++)
				tmp.Add(new PointF(x[i], y[i]));
					
			mapLines.Add(tmp.ToArray(typeof(PointF)));
		}
	

